{: .bad} classMockFoo{
public:
MOCK_METHOD(std::pair<bool,int>,GetPair,());//Won'tcompile!
MOCK_METHOD(bool,CheckMap,(std::map<int,double>,bool));//Won'tcompile!
};

{: .good} classMockFoo{
public:
MOCK_METHOD((std::pair<bool,int>),GetPair,());
MOCK_METHOD(bool,CheckMap,((std::map<int,double>),bool));
};

{: .good} classMockFoo{
public:
usingBoolAndInt=std::pair<bool,int>;
MOCK_METHOD(BoolAndInt,GetPair,());
usingMapIntDouble=std::map<int,double>;
MOCK_METHOD(bool,CheckMap,(MapIntDouble,bool));
};
